function validateEquation(a){return a.length>25?"Must not contain more than 25 symbols.":a.match(/[^\dxy\.\-+=*\/\(\)]+/)?"Does not contain valid equation symbols: 0123456789xy.-+=*/()":a.match(/[\dx*\-+\/]y=/)?"Must start with y=":a.match(/[^y]*y[^y]*y[^y]*/)?"Must contain one y.":a.match(/[^=]*=[^=]*=[^=]*/)?"Must contain one equals sign.":a.match(/[^x]*x[^x]*x[^x]*/)?"Must contain one x.":a.match(/([^\dxy][+*\/][\dxy])|([\dxy][-+\/*][^\dxy])|([\d][xy])/)?"Operators must be between variables or numbers":void 0}function generateGraphData(equation){for(var data=[],domain=Math.round(7*Math.random())+3,x=-1*domain;domain+1>x;x++){var y=eval(equation);data.push(x),data.push(y)}return data}$(document).ready(function(){var a=new Grid([]);$("#equation").change(function(){var b=validateEquation(this.value);b?alert(b):a=new Grid(generateGraphData(this.value))})});